let audioElement = null;
const SILENT_MP3_BASE64 = "data:audio/mpeg;base64,";
let isAudioUnlocked = false;

// New function to be called by your "Use camera" button
export function unlockAudio() {
  if (isAudioUnlocked) return;
  
  console.log("Attempting to unlock audio...");
  if (!audioElement) {
    audioElement = new Audio();
  }
  
  audioElement.src = SILENT_MP3_BASE64;
  audioElement.play()
    .then(() => {
      isAudioUnlocked = true;
      console.log("Audio unlocked successfully.");
    })
    .catch(err => {
      // This can still fail in some very strict browser contexts
      console.error("Audio unlock failed:", err);
    });
}

export function playAudio(audioUrl) {
  if (!audioUrl) return;

  // We reuse the same element that was unlocked
  if (!audioElement) {
    audioElement = new Audio();
  }
  
  audioElement.src = audioUrl;
  audioElement.load();
  audioElement.play()
    .then(() => console.log("Playing audio:", audioUrl))
    .catch(err => console.error("Audio play failed (is it unlocked?):", err));
}
